mapper.select:
SELECT * FROM( select ROW_NUMBER() OVER(PARTITION BY P.PNO ORDER BY nno asc) cnt, p.* FROM pmsnotice p WHERE 1=1 and pno=#{pno}
and title like '%'||#{title}||'%'
ORDER BY nno desc) WHERE cnt BETWEEN #{start} AND #{end}

mapper.update:
UPDATE pmsnotice
   SET views = views+1
 WHERE nno = #{no}
