mapper.select:
 		select count(*) 
		from pmstask
		where 1=1
		AND pno = #{pno}

		AND mno = #{mno}


		and tname like '%'||#{tname}||'%'

SELECT * FROM( select rownum cnt, b.* from PMSTASK b where 1=1
and tname like '%'||#{tname}||'%'
ORDER BY tno desc ) WHERE cnt BETWEEN #{start} AND #{end}

 		SELECT *
 		FROM(
			select rownum cnt, b.* 
			from PMSTASK b, pmsmember pm
			where 1=1 AND pm.mno = b.mno
			AND pm.pno = #{pno}

			AND pm.mno = #{mno}


			and tname like '%'||#{tname}||'%'

			ORDER BY tno DESC )
		WHERE cnt BETWEEN #{start} AND #{end}

mapper.update:
UPDATE PMSTASK
   SET tname = #{tname},
   	   detail = #{detail}   
 WHERE tno = #{tno}

UPDATE PMSTASK
   SET updetail = #{updetail}
 WHERE tno = #{tno}

UPDATE PMSTASK
   SET coment = #{coment}
 WHERE tno = #{tno}

UPDATE PMSTASK
   SET prog = #{prog}
 WHERE tno = #{tno}

UPDATE PMSTASK
   SET prog = 0
 WHERE tno = #{tno}

UPDATE PMSTASK
   SET tdiv = 22, pdate = sysdate
 WHERE tno = #{tno}

UPDATE PMSTASK
   SET tdiv = 23, pdate = sysdate
 WHERE tno = #{tno}

UPDATE PMSTASK
   SET tdiv = 24, pdate = sysdate
 WHERE tno = #{tno}
